<template lang="pug">
div( ref="chartWrap" class="chart-wrap")
</template>

<script lang="ts" setup>
import { ref, onMounted } from 'vue';
// @ts-ignore
import Chart from './Chart.js'

const imgBase64 = 'data:image/png;base64,'

const chartWrap = ref(null)

onMounted(() => {
  // 构建图表对象
  var chartObj = new Chart({ el: chartWrap.value })

  // 绘制圆形
  chartObj.push({
    type: 'circle',
    fillStyle: 'pink',
    x: 400,
    y: 300,
    r: 50
  })

  //  绘制线条
  chartObj.push({
    type: 'line',
    lineWidth: 4,
    data: [100, 90, 200, 90]
  })

  //  绘制矩形
  chartObj.push({
    type: 'rect',
    fillStyle: '#0f00ff',
    data: [350, 400, 100, 100]
  })

  //  绘制多边形
  chartObj.push({
    type: 'polygon',
    fillStyle: '#ffa500',
    lineWidth: '8',
    data: [
      [600, 200],
      [700, 200],
      [800, 300],
      [800, 400],
      [700, 500],
      [600, 300]
    ]
  })

  // 绘制图片
  chartObj.formatImg(imgBase64, (img:any) => {
    let x = 900
    let y = 50
    chartObj.push({
      type: 'img',
      img,
      x,
      y,
      deg: 0,
      center: [x + img.width / 2, y + img.height / 2],
      data: [
        [x, y],
        [x + img.width, y],
        [x + img.width, y + img.height],
        [x, y + img.height]
      ]
    })
  })
})

</script>

<style lang="stylus" scoped>
.chart-wrap
  height calc(100% - 40px)
  min-height 500px
  min-width 500px
  margin 20px
  box-shadow 0 0 3px orange
</style>
